head.script:
    	var FV_SEARCH_CNT = 10;		// 조회 건수
		var FV_nMAXCNT = -1;		// 전체 데이터 수	
    	var FV_sCustTypeSql = "";
    	var FV_CustSeq = "";		//고객번호
     	//var FV_sMngScId = ""; // 유저ID
     	/****************************************************************
		* 화면로드
		* @param  : 없음
