    	function fn_OnLoad(){
			wfrm_Page.visible(false);
			cb_CustType.removeAll();
     		cb_CustType.addItem("","ALL",0);
			// 상담차종

			setTitSendInfo("request", "", "", "mdms/mp/comm::VS_VARIANT_S03,mdms/mp/comm::CO_GROUP_S01");
        	setSubmissison("submissionCode");

        	edt_CustSeq.visible(false);
        	edt_PlanDtBk.visible(false);
        	edt_ActDtBk.visible(false);
        	edt_ActFrTimeBk.visible(false);
        	edt_ActToTimeBk.visible(false);
        	edt_RegDt.visible(false);        			

			edt_TaxpayNo.visible(false);
			cb_CompType.visible(false);
			cb_Dealer.visible(false);
			cb_CustType.visible(false);
			cb_DisuseYn.visible(false);
			output85.visible(false);
			output88.visible(false);
			output89.visible(false);
			output93.visible(false);

			if(GV_USER_TYPE != "23" || GV_USER_GROUP.indexOf("DS6") >= 0){
				ta_MngCmt.setReadOnly(false);
				document.getElementById("ta_MngCmt").className = "input_edit";
			}
		}
       	/****************************************************************
