        }
        /****************************************************************
		* 저장
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_Save(){   

        	var sRegKnd = rd_RegKnd.getValue();
        	var sPlanDt = "";
        	var sActFr = "";
        	var sActTo = "";

        	if(sRegKnd == "10"){
        		if(gfn_IsNullVal(edt_PlanDt.getValue()) ||

        			edt_PlanHour.getValue()=="" || edt_PlanMin.getValue()==""){
					alert("예정일자 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
					return;
				} else{
					if(!gfn_IsValidDate(edt_PlanDt.getFormattedValue())){
						alert("예정일자 값을 확인하여 주십시오.");
						edt_PlanDt.focus();
						return;
					}

					edt_PlanHour.setValue(gfn_IsValidHour(edt_PlanHour.getValue()));
					edt_PlanMin.setValue(gfn_IsValidHour(edt_PlanMin.getValue()));

					if(edt_PlanMin.getValue()%10 != 0){
						alert("예정일자 시간은 10분 단위로 입력가능합니다.");
						edt_PlanMin.focus();
						return;
					}
				}

				sPlanDt = edt_PlanDt.getValue().replaceAll("-","")+edt_PlanHour.getValue()+edt_PlanMin.getValue();
        	} else if(sRegKnd == "20"){
        		var sFrTime = edt_ActFrHour.getValue() + edt_ActFrMin.getValue();
        		var sToTime = edt_ActToHour.getValue() + edt_ActToMin.getValue();

        		if(gfn_IsNullVal(edt_ActDt.getValue()) || 
        		   	gfn_IsNullVal(edt_ActFrHour.getValue()) || gfn_IsNullVal(edt_ActFrMin.getValue()) || 
        		   	gfn_IsNullVal(edt_ActToHour.getValue()) || gfn_IsNullVal(edt_ActToMin.getValue())){
					alert("활동일시 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
					return;
				} else{
					if(!gfn_IsValidDate(edt_ActDt.getFormattedValue())){
						alert("활동일자 값을 확인하여 주십시오.");
						edt_ActDt.focus();
					}

					edt_ActFrHour.setValue(gfn_IsValidHour(edt_ActFrHour.getValue()));
					edt_ActFrMin.setValue(gfn_IsValidHour(edt_ActFrMin.getValue()));
					edt_ActToHour.setValue(gfn_IsValidHour(edt_ActToHour.getValue()));
					edt_ActToMin.setValue(gfn_IsValidHour(edt_ActToMin.getValue()));

					if(edt_ActFrMin.getValue()%10 != 0){
						alert("활동일시는 10분 단위로 입력가능합니다.");
						edt_ActFrMin.focus();
						return;
					}
					if(edt_ActToMin.getValue()%10 != 0){
						alert("활동일시는 10분 단위로 입력가능합니다.");
						edt_ActToMin.focus();
						return;
					}
				}

				if(!gfn_IsNullVal(sFrTime) && !gfn_IsNullVal(sToTime)){			

					if(sFrTime > sToTime){
						alert("종료시간이 시작시간 보다 작습니다.");
						edt_ActFrHour.focus();
						return;
					}

					if(sFrTime == sToTime){
						alert("시작시간과 종료시간이 달라야 합니다.");
						edt_ActToHour.focus();
						return;
					}
				}

				if(gfn_IsNullVal(cb_ActResult.getValue())){
					alert("활동결과 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
					cb_ActResult.focus();
					return;
				}

				sActFr = edt_ActFrHour.getValue() + ":" + edt_ActFrMin.getValue();
				sActTo = edt_ActToHour.getValue() + ":" + edt_ActToMin.getValue();
        	}        	

        	if(gfn_IsNullVal(rd_RegKnd.getValue())){
				alert("등록구분 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				rd_RegKnd.focus();
				return;
			}
			if(gfn_IsNullVal(edt_CustNm.getValue())){
				alert("고객 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				edt_CustNm.focus();
				return;
			}
			if(gfn_IsNullVal(edt_Title.getValue())){
				alert("제목 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				edt_Title.focus();
				return;
			}
			if(gfn_IsNullVal(cb_ActMethod.getValue())){
				alert("접촉방법 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				cb_ActMethod.focus();
				return;
			}
			if(gfn_IsNullVal(cb_ActKnd.getValue())){
				alert("활동구분 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				cb_ActKnd.focus();
				return;
			}
			alert(cb_Variant.getValue());
			if(cb_Variant.getValue() == ""){
				alert("상담차종 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				cb_Variant.focus();
				return;
			}

			//당일만 수정 가능			

			if(!gfn_IsNullVal(edt_RegDt.getValue())){
				if(edt_RegDt.getValue().replaceAll("-","") != gfn_GetDate(0,0,0).replaceAll("-","")){
					alert("3일 이내에 등록된 업무일지만 수정이 가능합니다.");
					return;
				}			

			} else{
				if(sRegKnd == "10"){
					if(edt_PlanDt.getValue() < gfn_GetDate(0,0,-3).replaceAll("-","")){
						alert("과거 3일 전의 업무일지는 등록할 수 없습니다.");
						edt_PlanDt.focus();
						return;
					}
				} else if(sRegKnd == "20"){
					if(edt_ActDt.getValue() < gfn_GetDate(0,0,-3).replaceAll("-","")){
						alert("과거 3일 전의 업무일지는 등록할 수 없습니다.");
						edt_ActDt.focus();
						return;
					}				

				}			

			}

			if(fn_IsDuplicateChk(sPlanDt))	return;			

			setTitSendInfo("request", "S310302_W01Action", "save", "");
			setInputParamData("CUST_SEQ",edt_CustSeq.getValue());
			setInputParamData("ACTIVE_SEQ",parent.FV_sActSeq);
//			setInputParamData("DEALER_ID",edt_MemoDealerId.getValue());
//			setInputParamData("MNG_SC_ID",parent.FV_sMngScId);
			setInputParamData("REG_KND",rd_RegKnd.getValue());
			setInputParamData("TITLE",edt_Title.getValue());
			setInputParamData("CONTENTS","");
			setInputParamData("PLAN_DT",sPlanDt);
			setInputParamData("ACTIVE_DT",edt_ActDt.getValue());
			setInputParamData("ACTIVE_METHOD",cb_ActMethod.getValue());
			setInputParamData("ACTIVE_KND",cb_ActKnd.getValue());
			setInputParamData("ACTIVE_RESULT",cb_ActResult.getValue());
			setInputParamData("ACTIVE_MODEL",cb_Variant.getValue());
			setInputParamData("REMARK",ta_Remark.getValue());
			setInputParamData("MNG_COMMENT",ta_MngCmt.getValue());
			setInputParamData("ACTIVE_FR_TIME",sActFr);
			setInputParamData("ACTIVE_TO_TIME",sActTo);
			setInputParamData("UPDATE_TYPE","INSERT");
			setInputParamData("MENU_ID","S310302_W01");		//메뉴아이디

			setSubmissison("submissionSave");

  			fn_AfterSave();
        }
        /****************************************************************
		* 삭제
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_Delete(){   

        	var sActKnd = cb_ActKnd.getValue();

        	if(sActKnd != "09" && sActKnd != "11"){
        		setTitSendInfo("request", "S310302_W01Action", "delete", "");

        		setInputParamData("CUST_SEQ",edt_CustSeq.getValue());
				setInputParamData("ACTIVE_SEQ",parent.FV_sActSeq);
				setInputParamData("UPDATE_TYPE","DELETE");
				setInputParamData("MENU_ID","S310302_W01");		//메뉴아이디

        		setSubmissison("submissionSave");

        		fn_AfterDelete();
        	} else{
        		alert("전시장고객응대 및 시승에 대한 업무일지는 \n삭제할 수 없습니다.");
        		return;
        	}
        }
        /****************************************************************
		* callback(저장)
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_AfterSave(){
        	var errCode = WebSquare.ModelUtil.getInstanceValue("resSave/root/params/param[@id='ErrorCode']");

        	if(errCode < 0){
        		alert("처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");
        	} else{
        		alert("저장을 완료했습니다.");

				parent.fn_Search();
        		parent.fn_PopupClose(true);
        	} 

        }
        /****************************************************************
		* callback(저장)
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_AfterDelete(){
        	var errCode = WebSquare.ModelUtil.getInstanceValue("resSave/root/params/param[@id='ErrorCode']");

        	if(errCode < 0){
        		alert("처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");
        	} else{
        		alert("삭제를 완료했습니다.");
        		parent.fn_PopupClose(true);
        	} 
        }
		/****************************************************************
		* 등록 변경 시 처리
		* @param  : 없음
		* @return : 없음
		******************************************************************/
		function fn_RegKndChange(){
			var sRegKnd = rd_RegKnd.getValue();
			var curDate = new Date();
		 	var dHour = curDate.getHours();
		 	var dMin = curDate.getMinutes();			

		 	if(dHour < 10) dHour = "0" + dHour;
		 	if(dMin < 10) dMin = "0" + dMin;

			dMin = dMin.toString().substring(0,1)+"0";

			if(sRegKnd == "10"){
				edt_PlanDt.setDisabled(false);
				edt_PlanHour.setDisabled(false);
				edt_PlanMin.setDisabled(false);
				var sPlanDtBk = edt_PlanDtBk.getValue();
				if(gfn_IsNullVal(parent.FV_sCustSeq) || gfn_IsNullVal(sPlanDtBk)){
					edt_PlanDt.setValue(gfn_GetDate(0,0,0).replaceAll("-",""));
					edt_PlanHour.setValue(dHour);
					edt_PlanMin.setValue(dMin);
				} else{
					edt_PlanDt.setValue(sPlanDtBk.substring(0,8));
					edt_PlanHour.setValue(sPlanDtBk.substring(8,10));
					edt_PlanMin.setValue(sPlanDtBk.substring(10,12));
				}

				edt_ActDt.setValue("");
				edt_ActDt.setDisabled(true);
				cb_ActResult.setDisabled(true);				
				edt_ActFrHour.setValue("");
				edt_ActFrMin.setValue("");
				edt_ActToHour.setValue("");
				edt_ActToMin.setValue("");
				edt_ActFrHour.setDisabled(true);
				edt_ActFrMin.setDisabled(true);
				edt_ActToHour.setDisabled(true);
				edt_ActToMin.setDisabled(true);
			} else if(sRegKnd == "20"){			 	
				edt_ActDt.setDisabled(false);
				cb_ActResult.setDisabled(false);

				if(gfn_IsNullVal(parent.FV_sCustSeq) || gfn_IsNullVal(edt_ActDtBk.getValue())){
					edt_ActDt.setValue(gfn_GetDate(0,0,0).replaceAll("-",""));					
				 	edt_ActFrHour.setValue(dHour);
				 	edt_ActFrMin.setValue(dMin);
				 	if(edt_ActFrMin.getValue() == "50"){
				 		edt_ActToHour.setValue(parseInt(dHour)+1);
				 		edt_ActToMin.setValue("00");
				 	} else{
				 		edt_ActToHour.setValue(dHour);
				 		edt_ActToMin.setValue(parseInt(dMin)+10);
				 	}
				} else{
					var sActFrTimeBk = edt_ActFrTimeBk.getValue();
					var sActToTimeBk = edt_ActToTimeBk.getValue();
					edt_ActDt.setValue(edt_ActDtBk.getValue());						
					edt_ActFrHour.setValue(sActFrTimeBk.substring(0,2));
				 	edt_ActFrMin.setValue(sActFrTimeBk.substring(3,5));
				 	edt_ActToHour.setValue(sActToTimeBk.substring(0,2));
				 	edt_ActToMin.setValue(sActToTimeBk.substring(3,5));
				}

				edt_ActFrHour.setDisabled(false);
				edt_ActFrMin.setDisabled(false);
				edt_ActToHour.setDisabled(false);				
				edt_ActToMin.setDisabled(false);
				edt_PlanDt.setValue("");
				edt_PlanHour.setValue("");
				edt_PlanMin.setValue("");
				edt_PlanDt.setDisabled(true);
				edt_PlanHour.setDisabled(true);
				edt_PlanMin.setDisabled(true);
			}
		}
		/****************************************************************
		* 고객정보 검색
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_CustSearch(){
        	parent.FV_sCustNm = edt_CustNm.getValue();
			parent.FV_sDealerId = GV_UP_GROUP_ID;
			parent.FV_sCustType = "A";

			switch_Popup.setSelectedIndex(1);

			if(parent.FV_sCustType == "P"){
     			cb_CustType.addItem("01","개인",1);
     			cb_CustType.addItem("04","등록외국인",2);
     			cb_CustType.addItem("05","기타 개인",3);
     			FV_sCustTypeSql = "AND A.CUST_TYPE IN('01','04','05')";
     		} else if(parent.FV_sCustType == "B"){
     			cb_CustType.addItem("02","법인",1); 
     			cb_CustType.addItem("03","개인사업자",2);
     			FV_sCustTypeSql = "AND A.CUST_TYPE IN('02','03')";
     		} else{
     			cb_CustType.addItem("01","개인",1);
     			cb_CustType.addItem("04","등록외국인",2);
     			cb_CustType.addItem("05","기타 개인",3);
     			cb_CustType.addItem("02","법인",4); 
     			cb_CustType.addItem("03","개인사업자",5);
     		}

			cb_Dealer.setValue(parent.FV_sDealerId);
	     	cb_CustType.setSelectedIndex(0);
	     	edt_CustName.setValue(edt_CustNm.getValue());

	     	if(!gfn_IsNullVal(edt_CustName.getValue()))
	     		fn_Search();

        }
        /****************************************************************
		* 조회
		* @param  : 없음
		* @return : 없음
		******************************************************************/
     	function fn_Search(pageNo){
     		if(pageNo == null){
				FV_nMAXCNT = -1;
				pageNo = 1;
				fn_InitPage();
			}

        	var sSqlid = "mdms/mp/comm:CU_BASE_S03@" + pageNo + "/" + FV_SEARCH_CNT;        	

        	setTitSendInfo("request", "", "", sSqlid);
			setInputParamData("CUST_TYPE_SQL", FV_sCustTypeSql);
			setInputParamData("DEALER_ID", cb_Dealer.getValue());
			setInputParamData("CUST_NM", edt_CustName.getValue());
			setInputParamData("CUST_TYPE", cb_CustType.getValue());
			setInputParamData("TAXPAY_NO", edt_TaxpayNo.getValue());
			setInputParamData("HP_AREA", cb_HpArea.getValue());
			setInputParamData("HP_NO", edt_Hp.getValue());
			setInputParamData("COMPANY_TYPE", cb_CompType.getValue());
			setInputParamData("DISUSE_YN", cb_DisuseYn.getValue());
			if(GV_USER_TYPE == "23")	
				setInputParamData("MNG_SC_ID", GV_USER_ID);

			setSubmissison("submissionList");
        }
        /****************************************************************
		* callback (조회)
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_AfterSearch(){
        	var nRownum = (FV_nCurrPage-1) * FV_SEARCH_CNT;

        	if(FV_nCurrPage == -1 && grd_List.getRowCount() > 0){
				FV_nMAXCNT = grd_List.getCellData(0,0);
				fn_SetPageInfo(1,FV_nMAXCNT,FV_SEARCH_CNT);
				nRownum = 0;
			}

			for(var i=0; i<grd_List.getRowCount(); i++){
        		grd_List.setCellData(i, 1, (i+1)+nRownum);
        	}
        }
        /****************************************************************
		* 고객정보
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SetParams(row,col){
        	if(col == 2){
        		edt_CustSeq.setValue(grd_List.getCellData(row,2));			//	고객번호
        		edt_CustNm.setValue(grd_List.getCellData(row,3));			//	고객명
        		edt_HpNo.setValue(grd_List.getCellData(row,10));			//	핸드폰


				//고객번호 셋팅
				FV_CustSeq = grd_List.getCellData(row,2);

				switch_Popup.setSelectedIndex(0);
			//	switch_Popup.setSelectedID("case1");
        	}
        }
