		* @return : 없음
		******************************************************************/
        function fn_CloseCust(){        
        	switch_Popup.setSelectedIndex(0);
        }
        /****************************************************************
		* 일정 중복 체크
		* @param  : 없음
		* @return : 없음
		******************************************************************/

        function fn_IsDuplicateChk(sPlanDt){
        	setTitSendInfo("request", "", "", "mdms/mp/act:S310302_W01_S02");
        	setInputParamData("PLAN_DT",sPlanDt);   

        	if(!gfn_IsNullVal(parent.FV_sCustSeq) && !gfn_IsNullVal(parent.FV_sActSeq)){
        		return false;
        	} else{        		
        		setInputParamData("MNG_SC_ID",GV_USER_ID); 
        	}

    		setSubmissison("submissionCnt");

    		var nCnt = WebSquare.ModelUtil.getInstanceValue("resCnt/root/dataset[@id='ds_Cnt']/record/CNT");

    		if(nCnt > 0){
    			alert("이미 등록된 일정이 있습니다. 확인 후 등록하시기 바랍니다.");
    			return true;
    		} else{
				return false;
    		}
        }
        fn_OnLoad();
