xf:submission.script:
		if(FV_sSvcNm == "T"){
			fn_SetContractFCST_T();
			fn_SetSalesFCST_T();
		}else{
			fn_SetContractFCST_L();
			fn_SetSalesFCST_L();
		}
		fn_setTitle(FV_sSvcNm);
   		parent.fn_SpinnerHide();

FV_DELIVERY_PERCENT = WebSquare.ModelUtil.getInstanceValue("resCond/root/dataset[@id='ds_Cond']/record/DELIVERY_PERCENT");
//jhkim, 2012-04-03, FV_DELIVERY_PERCENT null 처리
FV_DELIVERY_PERCENT = (FV_DELIVERY_PERCENT == "") ? "0" : FV_DELIVERY_PERCENT;
fn_Search();

head.script:
		var FV_sDealerId = parent.cb_LDealer.getValue(); //부모창의 딜러아이디.
       	var FV_sSvcNm = parent.FV_SvcNm; //서비스
		var FV_DELIVERY_PERCENT = ""; // 전역변수 (param)

		//달력에 현재 날자 넣기
		cal_date.setValue(gfn_GetDate(0,0,0));

		/*********************************************
		*토요타 또는 렉서스인 경우 서로 다른 그리드에 보여줌.
		*********************************************/
		if(FV_sSvcNm == "T"){
       		switch_brand.setSelectedIndex(0);
       	}else{
       		switch_brand.setSelectedIndex(1);
       	}

		/************************************
		*FV_DELIVERY_PERCENT 값 Setting
		************************************/
		function fn_weeks(){
			parent.fn_SpinnerShow();
			var sDate = gfn_addDate("d",-1,cal_date.getValue(),"-");
			var actionNm = "";
			if(FV_sSvcNm == "T"){
				actionNm = "TOYOTAAction";
			}
			setTitSendInfo("request", actionNm, "", "mdms/mp/eis:S210015_W01_S03");
	       	setInputParamData("APP_DATE", sDate);
	   		setSubmissison("submissionDeliverypercent");
		}

		/************************************
		*조회
		************************************/
		function fn_Search(){
			var actionNm = "";
			var sDate = gfn_addDate("d",-1,cal_date.getValue(),"-");
			if(FV_sSvcNm == "T"){
				actionNm = "TOYOTAAction";
			}

			setTitSendInfo("request", actionNm, "", "mdms/mp/eis:S210015_W01_S01,mdms/mp/eis:S210015_W01_S02");
	       	setInputParamData("DEALER_ID", FV_sDealerId);
	       	setInputParamData("APP_DATE", sDate);
	       	setInputParamData("TARGET_DAY", sDate.substr(8,2)); 
	       	setInputParamData("DELIVERY_PERCENT", FV_DELIVERY_PERCENT);
	       	setInputParamData("GV_UP_GROUP_ID", GV_UP_GROUP_ID);
	   		setSubmissison("submissionFCST");
	   	}

		/************************************
		*컬럼 설명값 Setting
		************************************/
		function fn_setTitle(svc){
			var sDate = gfn_addDate("d",-1,cal_date.getValue(),"-").substr(8,2);
			if(svc == "L"){
				grid1.setCellData(0,"column48","Daliy Target(~"+sDate+")");
				grid1.setCellData(0,"column64","Actual(~"+sDate+")");
				grid2.setCellData(0,"column161","② Contract(~"+sDate+")");
				grid2.setCellData(0,"column193","④ BOC(~"+sDate+")");
				grid2.setCellData(0,"column225","Delivery(⑤ × "+FV_DELIVERY_PERCENT+"%)");
				grid2.setCellData(0,"column241","Sales(~"+sDate+")");
			}else{
				grid3.setCellData(0,"column385","Daliy Target(~"+sDate+")");
				grid3.setCellData(0,"column401","Actual(~"+sDate+")");
				grid4.setCellData(0,"column513","② Contract(~"+sDate+")");
				grid4.setCellData(0,"column545","④ BOC(~"+sDate+")");
				grid4.setCellData(0,"column577","Delivery(⑤ × "+FV_DELIVERY_PERCENT+"%)");
				grid4.setCellData(0,"column593","Sales(~"+sDate+")");
			}
		}

		/************************************
		*Lexus Contract Grid
		************************************/
		function fn_SetContractFCST_L(){
			var contract_Fcst = WebSquare.ModelUtil.findInstanceNodes("resContractFCST/root/dataset[@id='ds_Contract']/record");
			grid1.insertRow(0);
			var total = [0,0,0,0,0,0,0];
			var header_cnt = 0;
   			for(var i=0; i<contract_Fcst.length; i++){
   				var sOrder = WebSquare.xml.getValue(contract_Fcst[i],"DISPLAY_ORDER");
   				var sGubun = WebSquare.xml.getValue(contract_Fcst[i],"GUBUN").substr(0,2);
				var sValue = WebSquare.xml.getValue(contract_Fcst[i],"VALUE");
				var sVariant = WebSquare.xml.getValue(contract_Fcst[i],"VARIANT_CD");
				switch(sGubun){
					case "01":
						grid1.setCellData(0,i+1,sValue);
						total[0] += parseInt(sValue);
						grid1.setHeaderValue("l_header_con_" + ++header_cnt,sVariant);
						break;
					case "02":
						grid1.setCellData(0,i+3,sValue);
						total[1] += parseInt(sValue);
						break;
					case "03":
						grid1.setCellData(0,i+5,sValue);
						total[2] += parseInt(sValue);
						break;
					case "04":
						grid1.setCellData(0,i+7,sValue);
						total[3] += parseInt(sValue);
						break;
					case "05":
						grid1.setCellData(0,i+9,sValue);
						total[4] += parseInt(sValue);
						break;
					case "06":
						grid1.setCellData(0,i+11,sValue);
						total[5] += parseInt(sValue);
						break;
					case "07":
						grid1.setCellData(0,i+13,sValue);
						total[6] += parseInt(sValue);
						grid1.setColumnColor(0,i+13,"#2fe9ee");
						break;
				}
				/*if(sGubun=="02Daily Target"){
					var total = 0;
					if(sOrder=="1"){
					}
					grid1.setCellData(0,i+3,sValue);
					total[1] += parseInt(sValue);
				}*/
			}
			grid1.setCellData(0,"column32",total[0]);
			grid1.setCellData(0,"column33",total[1]);
			grid1.setCellData(0,"column49",total[2]);
			grid1.setCellData(0,"column65",Math.round((total[2]/total[1])*100,1)+"%");
			grid1.setCellData(0,"column81",total[4]);
			grid1.setCellData(0,"column97",total[5]);
			grid1.setCellData(0,"column113",total[6]);
			grid1.setColumnColor("column112","#2fe9ee");
			grid1.setColumnColor("column128","#2fe9ee");
		}

