					telno = "080-555-3579";
					break;
				case "JB00000": //렉서스 대전
					telno = "042-500-3369";
					break;
				case "KM00000": //렉서스 부산
					telno = "051-601-0400";
					break;
				case "YM10101": //렉서스 포항
					telno = "080-293-7000";
