     	/****************************************************************
		* 상용구 저장
		* @param  : null
		* @return : null
		******************************************************************/ 
        function fn_SavePreparedMsg(){
			if(gfn_IsNullVal(cb_MsgType.getValue())){
				alert("메세지 타입을 선택해주시기 바랍니다.");
				document.getElementById("cb_MsgType").focus();
				return;
			}

			if(gfn_IsNullVal(ta_Data.getValue())){
				alert("메세지를 입력해 주시기 바랍니다.");
				document.getElementById("ta_Data").focus();
				return;
			}
			setTitSendInfo("request", "S310003_SMSAction", "save", "");

			setInputParamData("MSG_TYPE",cb_MsgType.getValue());
			setInputParamData("MSG",fn_TransSpecialString(ta_Data.getValue()));
			setInputParamData("TEST","seho\rseho\rseho\nseho\n");

  			setSubmissison("submissionMsgSave");
		}
