     	/****************************************************************
		* 팝업 닫기
		* @param  : arrRtn	리턴값
		* @return : 없음
		******************************************************************/
        function fn_PopupClose(arrRtn){
        	gfn_PopupClose();

        	if(arrRtn != null){
        		var arrVal = "";
        		var nRow = 0;

        		for(var i=0; i<arrRtn.length; i++){
        			arrVal = arrRtn[i].split("^");

        			if(gfn_IsNullVal(grd_RecvList.getMatchedIndex(2,arrVal[1],true,0,4))){
        				var arrRow = grd_RecvList.getMatchedIndex(2,"",true,0,5).toString().split(",");
        				nRow = eval(arrRow[0]);

        				grd_RecvList.setCellData(nRow, 1, arrVal[0]);
        				grd_RecvList.setCellData(nRow, 2, arrVal[1]);
        			}
        		}
        		st_Cnt.setValue(nRow+1);
        	}
        }
        /****************************************************************
		* 문자메시지 제한
		* @param  : data	문자메시지 (ta_Data.getValue)
		* @return : 없음
		******************************************************************/
        function fn_GetByteLength(data) {
		    var pos;
		    var msglen = 0;
			var nLength = data.length;

		    for(i=0; i<nLength; i++){
		        pos = data.charAt(i);

		        if (escape(pos).length > 4)
		            msglen += 2;
		        else
		            msglen++;
		    }

		    st_CharLeng.setValue(msglen);

		    if(msglen > 2000 ){
		        alert('문자메시지는 2000바이트를 넘을 수 없습니다.');
				ta_Data.setValue(ta_Data.getValue().substr(0,2000));
		    }
		}
		/****************************************************************
		* 수신정보 삭제
		* @param  : row	해당 row	col	해당 col
		* @return : 없음
		******************************************************************/
		function fn_DeleteList(row, col){
			if(col == 4){
				if(!gfn_IsNullVal(grd_RecvList.getCellData(row,3))){
					grd_RecvList.removeRow(row);
					grd_RecvList.insertRow(4);
					var nCnt = 0;
					for(var i=0; i<5; i++){
		        	   	grd_RecvList.setCellData(i, 0, i+1);
		        	   	
		        	   	if(!gfn_IsNullVal(grd_RecvList.getCellData(i,3)))
		        	   		nCnt++;
		        	}
		        	grd_RecvList.setFocusedCell(0,0,false);
		        	st_Cnt.setValue(nCnt);
		        }
			}
		}
		/****************************************************************
		* SMS 전송
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SendSms(){
        	if(st_Cnt.getValue() == 0){
        		alert("전송할 정보가 없습니다.");
        		return;
        	}
        	
        	var sReserve = "";
        	
        	if(gfn_IsNullVal(ta_Data.getValue())){
				alert("내용 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				ta_Data.focus();
				return;
			}
			if(gfn_IsNullVal(edt_CallBackNo.getValue())){
				alert("회신번호 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
				edt_CallBackNo.focus();
				return;
			}
			if(chk_Reserved.getValue() == "1"){
				if(gfn_IsNullVal(edt_ReserveDt.getValue())
					|| edt_ReserveHour.getValue()=="" || edt_ReserveMin.getValue()==""){
					alert("일시 항목에 값이 누락되었습니다.\n확인하여 주십시오.");
					return;
				} else{
					if(!gfn_IsValidDate(edt_ReserveDt.getFormattedValue())){
						alert("예약일자 값을 확인하여 주십시오.");
						edt_ReserveDt.focus();
						return;
					}
					
					edt_ReserveHour.setValue(gfn_IsValidHour(edt_ReserveHour.getValue()));
				}
				sReserve = edt_ReserveDt.getValue().replaceAll("-","") + edt_ReserveHour.getValue() + edt_ReserveMin.getValue();
			}

			setTitSendInfo("request", "S310003_W01Action", "save", "");

			//request에 수정된 xml을 추가
			//setInstanceNodeDataSetforTIT("ds_SendSP", grd_RecvList.getUnmatchedXML(2,"",true,0,5), "request/root");

			setInputParamData("CUST_NM",fn_MakeDataSetForSp(2));
			setInputParamData("CALL_NO",fn_MakeDataSetForSp(3));
			setInputParamData("CUST_SEQ",fn_MakeDataSetForSp(1));
			setInputParamData("CALLBACKNO",edt_CallBackNo.getValue());
			setInputParamData("DATA",ta_Data.getValue());
			setInputParamData("YEYAK_TIME",sReserve);
			setInputParamData("UPDATE_COUNT",st_Cnt.getValue());
/*			setInputParamData("RESERVED","91");
			setInputParamData("JEOBSU_NO","");
			setInputParamData("JEOBSU_SUB_NO","99");
			setInputParamData("SC_SEND","N");
			setInputParamData("COMMIT_MODE,"");*/

  			setSubmissison("submissionSend");

  			fn_AfterSendSms();
        }
        /****************************************************************
		* Multi Procedure Call용
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_MakeDataSetForSp(col){
        	var sColData = "";
        	for(var i=0; i<grd_RecvList.getRowCount(); i++){        		
        		if(!gfn_IsNullVal(grd_RecvList.getCellData(i, 3))){
       				sColData += grd_RecvList.getCellData(i, col) + "),(";
       			}
        	}

        	return sColData;
        }
