			 		   "1900"];
		var FV_nStallCnt = 0;
     	/****************************************************************
		* 화면로드
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_OnLoad(){   
        	edt_ShopCd.setValue(parent.FV_sShopCd);
        	edt_ShopNm.setValue(parent.FV_sShopNm);
        	cal_RealResvDt.setValue(gfn_GetDate(0,0,0));

        	fn_SvcFsTime();
        	//fn_Search();
        }
        /****************************************************************
		* 조회
		* @param  : 페이지번호
		* @return : 없음
		******************************************************************/ 
        function fn_Search(){
        	FV_Row = -1 ;
			FV_StCol = -1 ;
			FV_EndCol = -1 ;

			var sRealResvDt = cal_RealResvDt.getValue().replaceAll("-","");

        	setTitSendInfo("request", "", "", "mdms/mp/resvt:S310502_P01_S01");
        	setInputParamData("SHOP_CD", edt_ShopCd.getValue());
        	setInputParamData("REAL_RESV_DT", sRealResvDt);
    		setSubmissison("submissionStallList");

    		setTitSendInfo("request", "", "", "mdms/mp/resvt:S310502_P01_S02");
        	setInputParamData("SHOP_CD", edt_ShopCd.getValue());
        	setInputParamData("REAL_RESV_DT", sRealResvDt);
    		setSubmissison("submissionResvList");

    		setTitSendInfo("request", "", "", "mdms/mp/resvt:S310502_P01_S03");
        	setInputParamData("SHOP_CD", edt_ShopCd.getValue());
        	setInputParamData("REAL_RESV_DT", sRealResvDt);
    		setSubmissison("submissionWorkList");

    		setTitSendInfo("request", "", "", "mdms/mp/resvt:S310502_P01_S04");
        	setInputParamData("SHOP_CD", edt_ShopCd.getValue());
        	setInputParamData("REAL_RESV_DT", sRealResvDt);
    		setSubmissison("submissionBlockList");

    		fn_SetStallList();
    		fn_SetResvList();
    		fn_SetWorkList();
    		fn_SetSelfList();
    		fn_SetBlockList();

        }
        /****************************************************************
		* 화면에 작업가능하지 않는 시간 표시
		* @param  : 페이지번호
		* @return : 없음
		******************************************************************/ 
        function fn_SetStallList(){
        	grd_List.initGrid();
        	grd_List.setColumnBackgroundColor("ST_TEXT", "#dedcdc");
        	var bStarted = true;
			var bEnded = true;

			var lstStall = WebSquare.ModelUtil.findInstanceNodes("resStallList/root/dataset[@id='ds_StallList']/record");
			FV_nStallCnt = lstStall.length;

			for(var i=0; i<lstStall.length; i++){
        		var nRow = grd_List.insertRow();

    			grd_List.setCellData(nRow, 0, WebSquare.xml.getValue(lstStall[i],"STALL_NO"));
    			grd_List.setCellData(nRow, 1, WebSquare.xml.getValue(lstStall[i],"STALL_NM"));
    			grd_List.setCellData(nRow, 2, WebSquare.xml.getValue(lstStall[i],"ST_TEXT"));
    			grd_List.setCellData(nRow, 3, WebSquare.xml.getValue(lstStall[i],"SA_ID"));

    			var stHm = WebSquare.xml.getValue(lstStall[i],"ST_HM");
    			var endHm = WebSquare.xml.getValue(lstStall[i],"END_HM");

    			if(stHm.length > 0 && endHm.length > 0){
    				bStarted = false;
					bEnded = false;

    				for(var j=1; j<FV_TIME.length; j++){
    					if(stHm == FV_TIME[j-1])
    						bStarted = true;

    					if(bStarted){
    						if(endHm == FV_TIME[j-1])
    							bEnded = true;
    					}

    					if(bStarted){
    						if(bEnded){
    							grd_List.setCellBackgroundColor(i, j+2, "gray");
    						} else{
    							grd_List.setCellBackgroundColor(i, j+2, "white");
    						}
    					} else{
    						grd_List.setCellBackgroundColor(i, j+2, "gray");
    					}    				
    				}
    			}
    		}
        }
        /****************************************************************
		* 화면에 예약 정보를 표시
		* @param  : 페이지번호
		* @return : 없음
		******************************************************************/
        function fn_SetResvList(){
        	var bStarted = false;
			var bEnded = false;
        	var bRed = false ;

        	var lstResv = WebSquare.ModelUtil.findInstanceNodes("resResvList/root/dataset[@id='ds_ResvList']/record");

			for(var i=0; i<lstResv.length; i++){
				var sStallNo = WebSquare.xml.getValue(lstResv[i],"RESV_STALL_NO")
        		var nRow = grd_List.getMatchedIndex(0, sStallNo, true, 0, FV_nStallCnt-1);

        		var stHm = WebSquare.xml.getValue(lstResv[i],"REAL_RESV_ST_HM");
    			var endHm = WebSquare.xml.getValue(lstResv[i],"REAL_RESV_END_HM");

    			if(stHm.length > 0 && endHm.length > 0){
    				bStarted = false;
					bEnded = false;

    				for(var j=1; j<FV_TIME.length; j++){
    					if(stHm == FV_TIME[j-1]){
    						bStarted = true;
    						bRed = !bRed;
    					}
    					if(bStarted){
    						if(endHm == FV_TIME[j-1])
    							bEnded = true;
    					}

    					if(bStarted){
    						if(!bEnded){
    							if(bRed){
    								grd_List.setCellBackgroundColor(nRow, j+2, "#FF0000");
	    						} else{
	    							grd_List.setCellBackgroundColor(nRow, j+2, "#FF8000");
	    						}
    						} 
    					}    				
    				}
    			}
        	}
        }
        /****************************************************************
		* 화면에 진행중인 정보를 표시
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SetWorkList(){
        	var bStarted = true;
			var bEnded = true;

			var lstWork = WebSquare.ModelUtil.findInstanceNodes("resWorkList/root/dataset[@id='ds_WorkList']/record");

			for(var i=0; i<lstWork.length; i++){
        		var sStallNo = WebSquare.xml.getValue(lstWork[i],"STALL_NO")
        		var nRow = grd_List.getMatchedIndex(0, sStallNo, true, 0, FV_nStallCnt-1);

        		var stHm = WebSquare.xml.getValue(lstWork[i],"ST_HM");
    			var endHm = WebSquare.xml.getValue(lstWork[i],"END_HM");

    			if(stHm.length > 0 && endHm.length > 0){
    				bStarted = false;
					bEnded = false;

    				for(var j=1; j<FV_TIME.length; j++){
    					if(stHm >= FV_TIME[j-1] && stHm < FV_TIME[j])
    						bStarted = true;

    					if(bStarted){
    						if(endHm == FV_TIME[j-1]){
    							bStarted = false;
    							bEnded = true;
    						}
    					}

    					if(bStarted){
    						if(!bEnded){
    							grd_List.setCellBackgroundColor(nRow, j+2, "yellow");
    						}

    						if(endHm > FV_TIME[j-1] && endHm < FV_TIME[j]){
    							bStarted = false;
    							bEnded = true;
    						}
    					}		
    				}
    			}
    		}
        }
        /****************************************************************
		* 화면에 자신의 예약정보 정보를 표시
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SetSelfList(){
        	var sStallNo = edt_StallNo.getValue();
        	if(gfn_IsNullVal(sStallNo))	return;  


        	var bStarted = true;
			var bEnded = true;

       		var nRow = grd_List.getMatchedIndex(0, sStallNo, true, 0, FV_nStallCnt-1);       		
       		var stHm = edt_StHm.getValue();
   			var endHm = edt_EndHm.getValue();

   			if(stHm.length > 0 && endHm.length > 0){
   				bStarted = false;
				bEnded = false;
   				var nStCol = -1;

   				for(var j=1; j<FV_TIME.length; j++){
   					if(stHm == FV_TIME[j-1]){
   						nStCol = j;
   						bStarted = true;
   					}

   					if(bStarted){
   						if(endHm == FV_TIME[j-1]){
   							bEnded = true;
   							FV_Row = nRow;
							FV_StCol = nStCol;
							FV_EndCol = j-1;
   						}
   					}

   					if(bStarted){
   						if(!bEnded){
   							grd_List.setCellBackgroundColor(nRow, j+2, "blue");
   						}
   					}		
   				}
   			}
        }
        /****************************************************************
		* 화면에 BLOCKING된 시간을 표시
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SetBlockList(){
        	var bStarted = true;
			var bEnded = true;

			var lstBlock = WebSquare.ModelUtil.findInstanceNodes("resBlockList/root/dataset[@id='ds_BlockList']/record");

			for(var i=0; i<lstBlock.length; i++){
        		var sStallNo = WebSquare.xml.getValue(lstBlock[i],"STALL_NO")
        		var nRow = grd_List.getMatchedIndex(0, sStallNo, true, 0, FV_nStallCnt-1);

        		var stHm = WebSquare.xml.getValue(lstBlock[i],"ST_HM");
    			var endHm = WebSquare.xml.getValue(lstBlock[i],"END_HM");

    			if(stHm.length > 0 && endHm.length > 0){
    				bStarted = false;
					bEnded = false;

    				for(var j=1; j<FV_TIME.length; j++){
    					if(stHm >= FV_TIME[j-1] && stHm < FV_TIME[j])
    						bStarted = true;

    					if(bStarted){
    						if(endHm == FV_TIME[j-1]){
    							bStarted = false;
    							bEnded = true;
    						}
    						if(stHm == bEnded){
    							bStarted = true;
    							bEnded = false;
    						}
    					}

    					if(bStarted){
    						if(!bEnded){
    							grd_List.setCellBackgroundColor(nRow, j+2, "black");
    						}

    						if(endHm > FV_TIME[j-1] && endHm < FV_TIME[j]){
    							bStarted = false;
    							bEnded = true;
    						} else if(stHm == bEnded){
    							bStarted = false;
    							bEnded = true;
    						}
    					}		
    				}
    			}
    		}
        }
        /****************************************************************
		* 선택한 스톨시간 초기화
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_ClearPreviousSelection(row){
        	if(FV_Row < 0)	return;

        	if(FV_Row != row){
        		grd_List.setCellBackgroundColor(FV_Row, FV_StCol, "white");
        		if(FV_EndCol > 0){
        			for(var i=FV_StCol; i<=FV_EndCol; i++){
        				grd_List.setCellBackgroundColor(FV_Row, i, "white");
        			}
        		}
        		FV_Row = -1;
				FV_StCol = -1 ;
				FV_EndCol = -1 ;
				return;
        	}

        	if(FV_StCol > 0){
        		if(FV_EndCol > 0){
        			for(var i=FV_StCol; i<=FV_EndCol; i++){
        				grd_List.setCellBackgroundColor(FV_Row, i, "white");
        			}
        			FV_Row = -1;
					FV_StCol = -1 ;
					FV_EndCol = -1 ;
        		}        		
        	}
        }
        /****************************************************************
		* 그리드 셀 클릭 시
		* @param  : 없음
		* @return : 없음
		******************************************************************/
        function fn_SelectStall(row,col){
        	edt_StHm.setValue("");
        	edt_EndHm.setValue("");
        	edt_StallNo.setValue("");
        	edt_StallNm.setValue("");

        	if(col < 3)	return;

        	if(grd_List.getCellBackgroundColor(row,col) == "white"){

        		fn_ClearPreviousSelection(row);

				var sFsTime = WebSquare.ModelUtil.getInstanceValue("resSvcFsTime/root/dataset[@id='ds_SvcFsTime']/record/FS_TIME");

				if(gfn_IsNullVal(sFsTime)){
					if(FV_Row < 0){
						grd_List.setCellBackgroundColor(row, col, "blue");
						FV_Row = row;
						FV_StCol = col;
						FV_EndCol = -1;
						return;
					}

	        		if((col - FV_StCol) < 5){
	        			grd_List.setCellBackgroundColor(FV_Row, FV_StCol, "white");
	        			grd_List.setCellBackgroundColor(row, col, "blue");
						FV_Row = row;
						FV_StCol = col;
						FV_EndCol = -1;
						return;
	        		}

	        		for(var i=FV_StCol+1; i<=col; i++){
	        			if(grd_List.getCellBackgroundColor(row,i) != "white"){
	        				grd_List.setCellBackgroundColor(row, FV_StCol, "white");
		        			grd_List.setCellBackgroundColor(row, col, "blue");
							FV_Row = row;
							FV_StCol = col;
							FV_EndCol = -1;
							return;        			
	        			}        			
	        		}

	        		for(var i=FV_StCol; i<=col; i++){
	        			grd_List.setCellBackgroundColor(row, i, "blue");
	        		}

	        		FV_EndCol = col;
	        		edt_StHm.setValue(FV_TIME[FV_StCol-3]);
		        	edt_EndHm.setValue(FV_TIME[FV_EndCol-2]);
		        	edt_StallNo.setValue(grd_List.getCellData(row,0));
		        	edt_StallNm.setValue(grd_List.getCellData(row,1));
		        } else{
		        	grd_List.setCellBackgroundColor(row, col, "blue");
		        	FV_Row = row;
					FV_StCol = col;
		        	FV_EndCol = eval(sFsTime/5) + FV_StCol;

		        	for(var i=FV_StCol+1; i<=FV_EndCol-1; i++){
	        			if(grd_List.getCellBackgroundColor(row,i) != "white"){
	        				grd_List.setCellBackgroundColor(row, FV_StCol, "white");
							FV_Row = row;
							FV_EndCol = -1;
							return;        			
	        			}        			
	        		}

		        	for(var i=FV_StCol; i<=FV_EndCol-1; i++){
	        			grd_List.setCellBackgroundColor(row, i, "blue");
	        		}

		        	edt_StHm.setValue(FV_TIME[FV_StCol-3]);
		        	edt_EndHm.setValue(FV_TIME[FV_EndCol-3]);
		        	edt_StallNo.setValue(grd_List.getCellData(row,0));
		        	edt_StallNm.setValue(grd_List.getCellData(row,1));
		        }
        	}
        }
        /****************************************************************
		* 정기점검 소요시간
		* @param  : 없음
		* @return : 없음
		******************************************************************/
		function fn_SvcFsTime(){
			setTitSendInfo("request", "", "", "mdms/mp/resvt:S310502_P01_S05");
        	setInputParamData("SHOP_CD", edt_ShopCd.getValue());
        	setInputParamData("FS_CD", parent.FV_sSvcTypeFmsCd);

    		setSubmissison("submissionSvcFsTime");
		}
		/****************************************************************
		* 확인
		* @param  : 없음
		* @return : 없음
		******************************************************************/
		function fn_Confirm(){
			if(gfn_IsNullVal(edt_StHm.getValue()) || gfn_IsNullVal(edt_EndHm.getValue())){
				alert("대상 데이터를 선택하여 주십시오.");
				return;
			}

			var curDate = new Date();
		 	var dHour = curDate.getHours();
		 	var dMin = curDate.getMinutes();			
		 	if(dHour < 10) dHour = "0" + dHour;
		 	if(dMin < 10) dMin = "0" + dMin;
		 	var sCurDtHm = gfn_GetDate(0,0,0).replaceAll("-","") + dHour + dMin;
		 	var sRecvDtHm = cal_RealResvDt.getValue().replaceAll("-","") + edt_StHm.getValue().replace(":","");

		 	if(sCurDtHm > sRecvDtHm){
		 		alert("현재일자 이후 시간을 선택하세요.");
				return;
			}

		 	var arrResult = new Array();
       		var nRow = grd_List.getFocusedRowIndex();

       		arrResult[0] = edt_ShopCd.getValue();		//	서비스센터
       		arrResult[1] = cal_RealResvDt.getValue();	//	실예약일자
       		arrResult[2] = edt_StHm.getValue();		//	예약시작시분
			arrResult[3] = edt_EndHm.getValue();			//	예약종료시분
			arrResult[4] = edt_StallNo.getValue();		//	예약스톨코드
			arrResult[5] = edt_StallNm.getValue();		//	예약 스톨명

			parent.cb_ResvSaId.setValue(grd_List.getCellData(nRow, 3));
			parent.fn_StallParams(arrResult);				
		}

        fn_OnLoad();
